using DataLayer;
using SilentWeb.Services;
using System;
using System.Collections.Generic;
using System.Configuration;
using System.Data;
using System.Linq;
using System.Web;
using System.Web.UI;
using System.Web.UI.WebControls;

namespace SilentWeb.ToolPages
{
    public partial class Messages : System.Web.UI.Page
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            HttpCookie cookie = Request.Cookies["Logged"];
            Response.Write("<img id='loadingImage' width='100' height='100' style='position:absolute;margin-top:-100px;margin-left:-100px;top:50% !important;left:50% !important;' src='../Pictures/loading.gif'/>");
            Response.Write("<div id='content' class='container' style='display:none !important'>");
            if (cookie["select"] == null)
            {
                Response.Write("<div class='alert alert-warning'><strong>Warning!</strong>You haven't selected any smartphone.</div>");
            }
            else
            {
                string connectionString = ConfigurationManager.ConnectionStrings["SilentConnection"].ConnectionString;
                DataTable table = SqlHelper.GetSpecificInformation(connectionString, cookie["select"], "GetContacts");

                if (table.Rows.Count == 0)
                {
                    Response.Write("<div class='alerboott alert-info'><strong>Info!</strong>You don't have any applications installed.</div>");
                }
                else
                {
                    Response.Write("<div class='row'>");
                    Response.Write("<div class='col-sm-3'>");
                    Response.Write("<div class='vertical-menu'>");
                    for (int i=0; i<table.Rows.Count; i++)
                    {
                        DataRow row = table.Rows[i];
                        string picture;
                        if (!row["Picture"].ToString().Equals(""))
                        {
                            picture = Base64Helper.MakeUrlUnsafe(row["Picture"].ToString());
                        }
                        else
                        {
                            picture = "";
                        }
                        Response.Write("<a href='#' onclick=manageChatBox("+ i +","+ table.Rows.Count + ")><img class='img-responsive img-circle' width='40' height='40' src='data:image/png;base64, " + picture + "' style='display:inline-block !important'/><span class='label label-default'>" + row["ContactName"] +"</span></a>");
                    }
                    Response.Write("</div>");
                    Response.Write("</div>");
                    
                    
                    Response.Write("<div class='col-sm-9'>");
                    
                    for (int i=0; i<table.Rows.Count; i++)
                    {
                        DataRow row = table.Rows[i];
                        DataTable tableConv = SqlHelper.GetConversation(connectionString, cookie["select"], row["Number"].ToString());

                        if (tableConv.Rows.Count == 0)
                        {
                            Response.Write("<div id=" + i + " class='alerboott alert-info hidden'><strong>Info!</strong>You don't have a conversation with this contact.</div>");
                        }
                        else
                        {
                            Response.Write("<div id="+ i +" class='panel panel-default hidden'>");
                            Response.Write("<div class='panel-heading'>");
                            Response.Write("<span class='glyphicon glyphicon-comment'></span> Chat");
                            Response.Write("</div>");
                            Response.Write("<div class='panel-body'>");
                            Response.Write("<ul class='chat'>");

                            foreach (DataRow convRow in tableConv.Rows)
                            {
                                if (convRow["Type"].ToString().Equals("Inbox"))
                                {
                                    Response.Write("<li class='left clearfix'><span class='chat-img pull-left'>");
                                    string picture;
                                    if (!convRow["Picture"].ToString().Equals(""))
                                    {
                                        picture = Base64Helper.MakeUrlUnsafe(convRow["Picture"].ToString());
                                    }
                                    else
                                    {
                                        picture = "";
                                    }
                                    Response.Write("<img class='img-responsive img-circle' width='40' height='40' src='data:image/png;base64, " + picture + "' alt='../Pictures/user.png'/></span>");
                                    Response.Write("<div class='chat-body clearfix'>");
                                    Response.Write("<div class='header'>");
                                    Response.Write("<strong class='primary-font'>" + convRow["ContactName"].ToString() + "</strong><small class='pull-right text-muted'>");
                                    Response.Write("<span class='glyphicon glyphicon-time'></span>"+ convRow["Date"].ToString() +"</small>");
                                    Response.Write("</div>");
                                    string cls;
                                    if (convRow["State"].ToString().Equals("1"))
                                    {
                                        cls = "ok";
                                    }
                                    else
                                    {
                                        cls = "remove";
                                    }
                                    Response.Write("<p>"+ convRow["Body"].ToString() + "<span class='glyphicon glyphicon-"+ cls +"'></span></p>");
                                    Response.Write("</div>");
                                    Response.Write("</li>");
                                }
                                else
                                {
                                    Response.Write("<li class='right clearfix'><span class='chat-img pull-right'>");
                                    Response.Write("<img class='img-responsive img-circle' width='40' height='40' src='data:image/png;base64,'/></span>");
                                    Response.Write("<div class='chat-body clearfix'>");
                                    Response.Write("<div class='header'>");
                                    Response.Write("<small class='text-muted'><span class='glyphicon glyphicon-time'></span>" + convRow["Date"].ToString() + "</small>");
                                    Response.Write("<strong class='pull-right primary-font'>Targeted User</strong>");
                                    Response.Write("</div>");
                                    string cls;
                                    if (convRow["State"].ToString().Equals("1"))
                                    {
                                        cls = "ok";
                                    }
                                    else
                                    {
                                        cls = "remove";
                                    }
                                    Response.Write("<p>" + convRow["Body"].ToString() + "<span class='glyphicon glyphicon-" + cls + "'></span></p>");
                                    Response.Write("</div>");
                                    Response.Write("</li>");
                                }
                            }

                            Response.Write("</ul>");
                            Response.Write("</div>");
                            Response.Write("</div>");
                        }
                    }

                    Response.Write("</div>");
                    Response.Write("</div>");
                }
            }
            Response.Write("</div>");
        }
    }
}